"""
This is a script to generate test cases for the 'inside' operator in Verilog.

Usage: python generate_inside_test_cases.py <filename>

Example: python generate_inside_test_cases.py my_verilog_file.v
"""

import re

def transform_code(filename):
    with open(filename, "r") as f:
        code = f.read()

    # Find all instances of the 'inside' operator
    matches = re.findall(r'(inside\s+[\w\(\)\. ]+)', code)

    for match in matches:
        # Replace each instance with a test case
        new_code = re.sub(match, "test_case", code)
    
    return new_code

def main():
    import argparse
    parser = argparse.ArgumentParser()
    parser.add_argument("filename")
    args = parser.parse_args()

    transformed_code = transform_code(args.filename)

    print(transformed_code)

if __name__ == "__main__":
    main()