import re


def transform_width(s):
    """Transform signal bit width in Verilog/SystemVerilog."""
    # Search for a signal declaration and replace its width attribute.
    pattern = r"wire\s*?|reg\s*|\s*(?P<name>[a-zA-Z_]+)\[(?P<width>\d+)\]"
    
    def replace(m):
        return re.sub(r"\[(\d+)\]", "[10]", m.group(0))
    
    # Use regex to find signal declarations and apply replacement.
    s = re.sub(pattern, replace, s)
    return s